tutorial.final:
import { render } from 'preact';
import { useRef } from 'preact/hooks';

function App() {
  const input = useRef();

  function onClick() {
    input.current.focus();
  }

  return (
    <div>
      <input ref={input} defaultValue="Hello World!" />
      <button onClick={onClick}>Focus input</button>
    </div>
  );
}

render(<App />, document.getElementById("app"));
